create database UPWale_com;
use UPWale_com;
create table users(username varchar(15) PRIMARY KEY,email varchar(80),pass varchar(10));
create table products(id integer(4) PRIMARY KEY AUTO_INCREMENT,pname varchar(100),img1 varchar(200),img2 varchar(200),img3 varchar(200),img4 varchar(200),price decimal(7,2),brand varchar(30),ptype varchar(30),description varchar(1000));

create table carts(uid varchar(15),p_id integer(4),quantity integer(2)) Foreign Key uid REFERENCES users(username);

CREATE TABLE `upwale_com`.`carts` (`user_id` VARCHAR(15) NOT NULL , `product_id` INT(4) NOT NULL , `quantity` INT(1) NOT NULL , 
Foreign key 'user_id' REFERENCES users(username) , foreign key 'product_id' REFERENCES products(id) ) ENGINE = InnoDB;